import React from "react";

export default function WelcomeScreen({ onConnect }) {
  return (
    <div style={{
      minHeight: "100vh",
      background: "#100E0A",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      fontFamily: "'Montserrat', 'Arial', sans-serif",
      color: "#FFC32B"
    }}>
      {/* Logo */}
      <div style={{ marginBottom: 24 }}>
        <img
          src="/logo.png"
          alt="Reaxtiv Logo"
          style={{
            width: 140,
            height: 140,
            objectFit: "contain",
            borderRadius: 24,
            boxShadow: "0 0 32px #FFC32B55"
          }}
        />
      </div>
      {/* Brand */}
      <div style={{
        fontWeight: 700,
        fontSize: 44,
        letterSpacing: "0.02em",
        marginBottom: 10,
        fontFamily: "'Orbitron', 'Montserrat', 'Arial', sans-serif",
        color: "#FFC32B"
      }}>
        REAXTIV
      </div>
      {/* Slogan */}
      <div style={{
        color: "#FFC32B",
        fontWeight: 600,
        fontSize: 20,
        marginBottom: 36,
        fontFamily: "'Luckiest Guy', 'Arial Black', cursive"
      }}>
        Code your voice
      </div>
      {/* Connect Button */}
      <button
        onClick={onConnect}
        style={{
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 20,
          border: "none",
          borderRadius: 40,
          padding: "16px 44px",
          marginBottom: 18,
          boxShadow: "0 2px 8px #FFC32B33",
          fontFamily: "'Luckiest Guy', 'Arial Black', cursive",
          cursor: "pointer",
          transition: "background 0.2s"
        }}
      >
        Connect wallet
      </button>
      {/* Privacy Note */}
      <div style={{
        color: "#fff",
        fontWeight: 700,
        fontSize: 16,
        marginTop: 0,
        textAlign: "center",
        textShadow: "0 1px 2px #000",
        fontFamily: "'Montserrat', 'Arial', sans-serif",
        maxWidth: 320,
        lineHeight: "1.2"
      }}>
        Your privacy, your control. No email or<br />phone required
      </div>
    </div>
  );
}
