import React, { useState } from "react";
import SendScreen from "./components/SendScreen";
import WalletSigned from "./components/WalletSigned";
import "./App.css";

// Simple bubble icon
const SimpleBubbleIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <rect x="4" y="7" width="24" height="15" rx="6" stroke={color} strokeWidth="2" />
    <path d="M8 22 L6 27 L14 22" stroke={color} strokeWidth="2" fill="none"/>
  </svg>
);
// Groups icon
const GroupsIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <circle cx="10" cy="14" r="4" stroke={color} strokeWidth="2" fill="none"/>
    <circle cx="22" cy="14" r="4" stroke={color} strokeWidth="2" fill="none"/>
    <ellipse cx="16" cy="22" rx="8" ry="4" stroke={color} strokeWidth="2" fill="none"/>
  </svg>
);
// Send (paper plane)
const SendIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <polygon points="4,28 28,16 4,4 8,16" fill="none" stroke={color} strokeWidth="2"/>
  </svg>
);
// Gear icon for settings
const SimpleGearIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <circle cx="16" cy="16" r="6" stroke={color} strokeWidth="2"/>
    <g stroke={color} strokeWidth="2">
      <path d="M16 3v4" />
      <path d="M16 25v4" />
      <path d="M3 16h4" />
      <path d="M25 16h4" />
      <path d="M8.4 8.4l2.8 2.8" />
      <path d="M21.2 21.2l2.8 2.8" />
      <path d="M21.2 10.8l2.8-2.8" />
      <path d="M8.4 23.6l2.8-2.8" />
    </g>
  </svg>
);

function GroupsScreen() {
  return (
    <div
      style={{
        minHeight: "calc(100vh - 110px)",
        background: "#181511",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: "28px 0 0 0",
        boxSizing: "border-box",
      }}
    >
      <div className="big-title">RΞAXTIV</div>
      <div className="big-subtitle">Groups</div>
      <div style={{ fontSize: 32, color: "#fff", textAlign: "center", fontWeight: 500 }}>
        Here you will see your groups and DAOs.<br />
        (Sample screen. Add your group list and features here.)
      </div>
    </div>
  );
}

function ChatsScreen() {
  return (
    <div
      style={{
        minHeight: "calc(100vh - 110px)",
        background: "#181511",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        boxSizing: "border-box",
      }}
    >
      <div className="big-title">RΞAXTIV</div>
      <div className="big-subtitle">Chats</div>
      <div style={{ fontSize: 32, color: "#fff", textAlign: "center", fontWeight: 500 }}>
        (Aquí iría tu lista de chats. Integra tu lógica real aquí.)
      </div>
    </div>
  );
}

function SettingsScreen({ onDisconnect }) {
  return (
    <div
      style={{
        minHeight: "calc(100vh - 110px)",
        background: "#181511",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        boxSizing: "border-box",
      }}
    >
      <div className="big-title">RΞAXTIV</div>
      <div className="big-subtitle">Settings</div>
      <div style={{ fontSize: 32, color: "#fff", textAlign: "center", fontWeight: 500, marginBottom: 32 }}>
        Ajusta tus preferencias y privacidad aquí.<br />
        (Aquí pueden ir tus opciones de usuario.)
      </div>
      <button className="big-button" onClick={onDisconnect}>
        Disconnect
      </button>
    </div>
  );
}

export default function App() {
  const [activeMenu, setActiveMenu] = useState("wallet");
  const [wallet, setWallet] = useState("0xA1b2C3d4E5f6a7b8c9d0Ef12Ab34Cd56Ef78De90");

  return (
    <div style={{ minHeight: "100vh", width: "100vw", background: "#181511", position: "relative" }}>
      {/* Main panel */}
      <div style={{ width: "100%", minHeight: "calc(100vh - 110px)" }}>
        {activeMenu === "wallet" && (
          <WalletSigned
            wallet={wallet}
            onOk={() => setActiveMenu("chats")}
            onDisconnect={() => setActiveMenu("wallet")}
          />
        )}
        {activeMenu === "chats" && <ChatsScreen />}
        {activeMenu === "send" && <SendScreen />}
        {activeMenu === "groups" && <GroupsScreen />}
        {activeMenu === "settings" && (
          <SettingsScreen onDisconnect={() => setActiveMenu("wallet")} />
        )}
      </div>
      {/* Bottom nav */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          width: "100%",
          position: "fixed",
          bottom: 0,
          left: 0,
          background: "#181511",
          padding: "18px 0 9px 0",
          borderTop: "1.5px solid #232323",
          zIndex: 40,
        }}
      >
        <div style={{ textAlign: "center", cursor: "pointer" }} onClick={() => setActiveMenu("chats")}>
          <SimpleBubbleIcon color={activeMenu === "chats" ? "#FFD700" : "#FFC32B"} />
          <div
            style={{
              color: activeMenu === "chats" ? "#FFD700" : "#FFC32B",
              fontFamily: "'Piedra', cursive",
