import React, { useState } from "react";
import WalletSigned from "./components/WalletSigned";
import ChatsScreen from "./components/ChatsScreen";

export default function App() {
  const [wallet, setWallet] = useState(null);
  const [signed, setSigned] = useState(false);

  // Logo local desde /public/logo.png
  const Logo = () => (
    <img
      src="/logo.png"
      alt="logo"
      style={{
        width: 110,
        marginBottom: 34,
        filter: "drop-shadow(0 0 20px #FFC32B)",
        borderRadius: 24,
        background: "#232323"
      }}
    />
  );

  const connectWallet = async () => {
    if (typeof window.ethereum !== "undefined") {
      try {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        if (accounts && accounts[0]) {
          setWallet(accounts[0]);
        }
      } catch (err) {
        alert("Connection rejected.");
      }
    } else {
      alert("Please install Metamask.");
    }
  };

  const disconnect = () => {
    setWallet(null);
    setSigned(false);
  };

  // Pantalla de bienvenida
  if (!wallet) {
    return (
      <div
        style={{
          minHeight: "100vh",
          background: "#181511",
          color: "#FFC32B",
          fontFamily: "'Piedra', cursive",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Logo />
        <h1
          style={{
            fontFamily: "'Orbitron', sans-serif",
            fontWeight: 700,
            fontSize: 44,
            letterSpacing: "0.04em",
            color: "#FFC32B",
            marginBottom: 8,
            textShadow: "0 2px 10px #FFD700",
          }}
        >
          RÎžAXTIV
        </h1>
        <div
          style={{
            fontFamily: "'Piedra', cursive",
            fontSize: 28,
            marginBottom: 38,
            color: "#FFC32B",
            textAlign: "center",
            maxWidth: 360,
            lineHeight: 1.25
          }}
        >
          Code your voice
        </div>
        <button
          onClick={connectWallet}
          style={{
            background: "#FFC32B",
            color: "#181511",
            fontWeight: 700,
            fontSize: 24,
            border: "none",
            borderRadius: 30,
            padding: "16px 46px",
            fontFamily: "'Piedra', cursive",
            cursor: "pointer",
            marginTop: 12,
          }}
        >
          Connect Wallet
        </button>
        <div
          style={{
            color: "#fff",
            fontWeight: 400,
            fontSize: 18,
            marginTop: 32,
            textAlign: "center",
            fontFamily: "'Piedra', cursive",
            maxWidth: 340,
            lineHeight: "1.25",
            opacity: 0.7
          }}
        >
          No email or phone required. 100% non-custodial.
        </div>
      </div>
    );
  }

  // Pantalla de wallet firmada
  if (wallet && !signed) {
    return (
      <WalletSigned
        wallet={wallet}
        onOk={() => setSigned(true)}
        onDisconnect={disconnect}
      />
    );
  }

  // Pantalla principal de chats
  return <ChatsScreen onDisconnect={disconnect} />;
}
