import React, { useState } from "react";
import ChatsScreen from "./components/ChatsScreen";
import WalletSigned from "./components/WalletSigned";

export default function App() {
  const [wallet, setWallet] = useState(null);

  return (
    <div>
      {!wallet ? (
        // Pantalla para conectar wallet (puedes cambiarlo por tu propio login/wallet connect)
        <div
          style={{
            minHeight: "100vh",
            background: "#181511",
            color: "#FFC32B",
            fontFamily: "'Piedra', cursive",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1
            style={{
              fontFamily: "'Orbitron', sans-serif",
              fontWeight: 700,
              fontSize: 40,
              letterSpacing: "0.04em",
              color: "#FFC32B",
              marginBottom: 12,
              textShadow: "0 2px 10px #FFD700",
            }}
          >
            RÎžAXTIV
          </h1>
          <div
            style={{
              fontFamily: "'Piedra', cursive",
              fontSize: 32,
              marginBottom: 32,
              color: "#FFC32B",
            }}
          >
            Welcome!
          </div>
          <button
            onClick={() =>
              setWallet("0xA1b2C3d4E5f6a7b8c9d0Ef12Ab34Cd56Ef78De90")
            }
            style={{
              background: "#FFC32B",
              color: "#181511",
              fontWeight: 700,
              fontSize: 24,
              border: "none",
              borderRadius: 30,
              padding: "16px 46px",
              fontFamily: "'Piedra', cursive",
              cursor: "pointer",
              transition: "background 0.2s",
              marginTop: 24,
            }}
          >
            Connect Wallet
          </button>
          <div
            style={{
              color: "#fff",
              fontWeight: 400,
              fontSize: 20,
              marginTop: 28,
              textAlign: "center",
              fontFamily: "'Piedra', cursive",
              maxWidth: 340,
              lineHeight: "1.25",
            }}
          >
            Your privacy, your control. No email or phone required
          </div>
        </div>
      ) : (
        <ChatsScreen onDisconnect={() => setWallet(null)} />
      )}
    </div>
  );
}
