import React from "react";

export default function WelcomeScreen({ onConnect }) {
  return (
    <div style={{
      minHeight: "100vh",
      background: "#100E0A",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center"
    }}>
      {/* Logo */}
      <div style={{ marginBottom: 32 }}>
        <img
          src="/logo.png"
          alt="RÎžAXTIV Logo"
          style={{
            width: 350,
            height: 350,
            objectFit: "contain",
            borderRadius: 40,
            boxShadow: "0 0 42px #FFC32B88"
          }}
        />
      </div>
      {/* Brand */}
      <div style={{
        fontFamily: "'Orbitron', sans-serif",
        fontWeight: 700,
        fontSize: 62,
        letterSpacing: "0.03em",
        marginBottom: 18,
        color: "#FFC32B"
      }}>
        R&#x039E;AXTIV
      </div>
      {/* Slogan */}
      <div style={{
        color: "#FFC32B",
        fontWeight: 400,
        fontSize: 24,
        marginBottom: 38,
        fontFamily: "'Piedra', cursive",
        textShadow: "0 1px 2px #000"
      }}>
        Code your voice
      </div>
      {/* Connect Button */}
      <button
        onClick={onConnect}
        style={{
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 22,
          border: "none",
          borderRadius: 40,
          padding: "18px 54px",
          marginBottom: 20,
          boxShadow: "0 2px 12px #FFC32B33",
          fontFamily: "'Piedra', cursive",
          cursor: "pointer",
          transition: "background 0.2s"
        }}
      >
        Connect wallet
      </button>
      {/* Privacy Note */}
      <div style={{
        color: "#fff",
        fontWeight: 400,
        fontSize: 18,
        marginTop: 0,
        textAlign: "center",
        textShadow: "0 1px 2px #000",
        fontFamily: "'Piedra', cursive",
        maxWidth: 340,
        lineHeight: "1.25"
      }}>
        Your privacy, your control. No email or<br />phone required
      </div>
    </div>
  );
}
