import React from "react";

export default function SettingsScreen({ onDisconnect }) {
  return (
    <div
      style={{
        minHeight: "calc(100vh - 110px)",
        background: "#000000",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: 24
      }}
    >
      <div
        style={{
          fontFamily: "'Orbitron', sans-serif",
          fontWeight: 700,
          fontSize: 38,
          letterSpacing: "0.04em",
          color: "#FFC32B",
          marginBottom: 22,
        }}
      >
        Settings
      </div>
      <div
        style={{
          fontSize: 22,
          color: "#fff",
          marginBottom: 36,
          textAlign: "center"
        }}
      >
        Adjust your preferences.<br />
        (Add your user options here.)
      </div>
      <button
        onClick={onDisconnect}
        style={{
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 22,
          border: "none",
          borderRadius: 30,
          padding: "18px 48px",
          fontFamily: "'Piedra', cursive",
          cursor: "pointer",
          marginTop: 16,
          boxShadow: "0 0 16px #FFD700"
        }}
      >
        Disconnect
      </button>
    </div>
  );
}
