import React, { useState } from "react";

export default function SettingsScreen({ onDisconnect }) {
  const [section, setSection] = useState("main");
  const [showLogout, setShowLogout] = useState(false);

  // Pantallas secundarias:
  if (section === "profile") return <ProfileScreen onBack={() => setSection("main")} />;
  if (section === "notifications") return <NotificationsScreen onBack={() => setSection("main")} />;
  if (section === "privacy") return <PrivacyScreen onBack={() => setSection("main")} />;
  if (section === "language") return <LanguageScreen onBack={() => setSection("main")} />;
  if (section === "wallet") return <WalletScreen onBack={() => setSection("main")} />;

  return (
    <div
      style={{
        minHeight: "calc(100vh - 110px)",
        background: "#000000",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        padding: 0,
        position: "relative"
      }}
    >
      <div
        style={{
          fontFamily: "'Orbitron', sans-serif",
          fontWeight: 700,
          fontSize: 38,
          letterSpacing: "0.04em",
          color: "#FFC32B",
          marginBottom: 0,
          paddingLeft: 26,
          paddingTop: 34,
        }}
      >
        RΞAXTIV
      </div>
      <div
        style={{
          fontFamily: "'Piedra', cursive",
          fontSize: 32,
          color: "#FFC32B",
          marginBottom: 36,
          paddingLeft: 26,
        }}
      >
        Settings
      </div>
      <div style={{ width: "92%", margin: "0 auto" }}>
        <div style={settingRowStyle} onClick={() => setSection("profile")}>
          <span style={activeLabelStyle}>Profile</span>
          <span style={inactiveLabelStyle}>Edit</span>
        </div>
        <div style={settingRowStyle} onClick={() => setSection("notifications")}>
          <span style={activeLabelStyle}>Notifications</span>
          <span style={inactiveLabelStyle}>On</span>
        </div>
        <div style={settingRowStyle} onClick={() => setSection("privacy")}>
          <span style={activeLabelStyle}>Privacy & Security</span>
          <span style={inactiveLabelStyle}>&gt;</span>
        </div>
        <div style={settingRowStyle} onClick={() => setSection("language")}>
          <span style={activeLabelStyle}>Language</span>
          <span style={inactiveLabelStyle}>EN</span>
        </div>
        <div style={settingRowStyle} onClick={() => setSection("wallet")}>
          <span style={activeLabelStyle}>Wallet & Tokens</span>
        </div>
        <div
          style={{
            ...settingRowStyle,
            marginBottom: 0,
            color: "#FFC32B",
            background: "#232323",
          }}
          onClick={() => setShowLogout(true)}
        >
          <span style={activeLabelStyle}>Log Out</span>
        </div>
      </div>
      {showLogout && (
        <LogoutDialog
          onConfirm={() => { setShowLogout(false); onDisconnect(); }}
          onCancel={() => setShowLogout(false)}
        />
      )}
    </div>
  );
}

const settingRowStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  background: "#232323",
  borderRadius: 20,
  padding: "18px 22px",
  marginBottom: 18,
  fontFamily: "'Piedra', cursive",
  fontSize: 21,
  fontWeight: 700,
  cursor: "pointer"
};

const activeLabelStyle = {
  color: "#FFC32B",
  fontWeight: 700,
};

const inactiveLabelStyle = {
  color: "#fff",
  fontWeight: 400,
};


// --- Pantallas secundarias ---

function ProfileScreen({ onBack }) {
  return (
    <SectionShell title="Profile" onBack={onBack}>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>Name: <input style={inputStyle} placeholder="Your Name" /></div>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>Avatar: <input style={inputStyle} placeholder="Upload" type="file" /></div>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>Bio: <textarea style={{ ...inputStyle, height: 60 }} placeholder="Short bio..." /></div>
      <button style={saveButtonStyle}>Save</button>
    </SectionShell>
  );
}

function NotificationsScreen({ onBack }) {
  return (
    <SectionShell title="Notifications" onBack={onBack}>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>Push Notifications: <input type="checkbox" /></div>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>Email Alerts: <input type="checkbox" /></div>
      <button style={saveButtonStyle}>Save</button>
    </SectionShell>
  );
}

function PrivacyScreen({ onBack }) {
  return (
    <SectionShell title="Privacy & Security" onBack={onBack}>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>
        <label>
          <input type="checkbox" /> Show profile publicly
        </label>
      </div>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>
        <label>
          <input type="checkbox" /> Enable 2FA
        </label>
      </div>
      <button style={saveButtonStyle}>Save</button>
    </SectionShell>
  );
}

function LanguageScreen({ onBack }) {
  return (
    <SectionShell title="Language" onBack={onBack}>
      <div style={{ color: "#fff", fontSize: 22, marginBottom: 14 }}>Choose your language:</div>
      <ul style={{ listStyle: "none", padding: 0, color: "#FFC32B", fontSize: 20 }}>
        <li style={langItemStyle}><input type="radio" name="lang" /> English</li>
        <li style={langItemStyle}><input type="radio" name="lang" /> Español</li>
        <li style={langItemStyle}><input type="radio" name="lang" /> Français</li>
        <li style={langItemStyle}><input type="radio" name="lang" /> Deutsch</li>
        <li style={langItemStyle}><input type="radio" name="lang" /> 中文</li>
      </ul>
    </SectionShell>
  );
}

function WalletScreen({ onBack }) {
  return (
    <SectionShell title="Wallet & Tokens" onBack={onBack}>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>
        Wallet: <span style={{ color: "#FFC32B" }}>0x1234...abcd</span>
      </div>
      <div style={{ color: "#fff", fontSize: 18, marginBottom: 18 }}>
        Tokens: <span style={{ color: "#FFC32B" }}>USDT, ETH, DAI</span>
      </div>
    </SectionShell>
  );
}

function LogoutDialog({ onConfirm, onCancel }) {
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.85)", zIndex: 200, display: "flex", alignItems: "center", justifyContent: "center"
    }}>
      <div style={{
        background: "#232323", borderRadius: 18, padding: 40, minWidth: 280, textAlign: "center"
      }}>
        <div style={{ color: "#FFC32B", fontSize: 28, marginBottom: 28 }}>Log Out</div>
        <div style={{ color: "#fff", fontSize: 20, marginBottom: 32 }}>Are you sure you want to disconnect your wallet?</div>
        <button onClick={onConfirm} style={{ ...saveButtonStyle, background: "#FFC32B", color: "#181511", marginRight: 18 }}>Yes</button>
        <button onClick={onCancel} style={{ ...saveButtonStyle, background: "#181511", color: "#FFC32B", border: "1.5px solid #FFC32B" }}>No</button>
      </div>
    </div>
  );
}

function SectionShell({ title, onBack, children }) {
  return (
    <div style={{
      minHeight: "calc(100vh - 110px)",
      background: "#000000",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      padding: 0,
      position: "relative"
    }}>
      <div style={{ display: "flex", alignItems: "center", paddingTop: 34, marginBottom: 24 }}>
        <button onClick={onBack} style={{
          marginLeft: 16,
          background: "none",
          border: "none",
          color: "#FFC32B",
          fontSize: 26,
          cursor: "pointer",
          marginRight: 20
        }}>←</button>
        <div
          style={{
            fontFamily: "'Piedra', cursive",
            fontSize: 32,
            color: "#FFC32B"
          }}
        >
          {title}
        </div>
      </div>
      <div style={{ width: "92%", margin: "0 auto" }}>
        {children}
      </div>
    </div>
  );
}

const inputStyle = {
  background: "#232323",
  color: "#FFC32B",
  border: "none",
  borderRadius: 12,
  padding: "8px 14px",
  fontSize: 18,
  marginLeft: 12,
  marginTop: 4,
  fontFamily: "'Piedra', cursive",
  width: 180,
};

const saveButtonStyle = {
  background: "#FFC32B",
  color: "#181511",
  fontWeight: 700,
  fontSize: 18,
  border: "none",
  borderRadius: 16,
  padding: "11px 32px",
  fontFamily: "'Piedra', cursive",
  cursor: "pointer",
  marginTop: 18,
  marginBottom: 10,
  boxShadow: "0 0 10px #FFD700"
};

const langItemStyle = {
  marginBottom: 10,
  cursor: "pointer"
};
