import React, { useState } from "react";
import Chats from "./components/Chats";
import Groups from "./components/Groups";
import SendTokens from "./components/SendTokens";
import Settings from "./components/Settings";
import "./index.css";

// Definimos las pantallas posibles de la app
const SCREENS = {
  WELCOME: "WELCOME",
  CHATS: "CHATS",
  GROUPS: "GROUPS",
  SEND: "SEND",
  SETTINGS: "SETTINGS",
};

export default function App() {
  const [screen, setScreen] = useState(SCREENS.WELCOME);
  const [wallet, setWallet] = useState(null);

  // Simula la conexión de una wallet
  const connectWallet = () => {
    setWallet("0xA1a3...eF91"); // Valor simulado
    setScreen(SCREENS.CHATS);
  };

  // Menú inferior para navegar
  const BottomNav = () => (
    <nav className="bottom-nav">
      <button onClick={() => setScreen(SCREENS.CHATS)}>Chats</button>
      <button onClick={() => setScreen(SCREENS.SEND)}>Send</button>
      <button onClick={() => setScreen(SCREENS.GROUPS)}>Groups</button>
      <button onClick={() => setScreen(SCREENS.SETTINGS)}>Settings</button>
    </nav>
  );

  // Pantalla de bienvenida
  if (screen === SCREENS.WELCOME)
    return (
      <div className="welcome-screen">
        <div className="logo-wallet" />
        <h1 className="logo-text">REAXTIV</h1>
        <h2 className="subtitle">Code your voice</h2>
        <button className="connect-btn" onClick={connectWallet}>
          Connect wallet
        </button>
        <p className="privacy-text">
          Your privacy, your control. No email or phone required
        </p>
      </div>
    );

  // Resto de pantallas
  return (
    <div className="app-container">
      {screen === SCREENS.CHATS && <Chats />}
      {screen === SCREENS.GROUPS && <Groups />}
      {screen === SCREENS.SEND && <SendTokens />}
      {screen === SCREENS.SETTINGS && (
        <Settings wallet={wallet} onLogout={() => {
          setWallet(null);
          setScreen(SCREENS.WELCOME);
        }} />
      )}
      <BottomNav />
    </div>
  );
}
