import React, { useState } from "react";

// SVG ICONS
const ChatIcon = ({ color = "#FFC32B", size = 34, filled }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill={filled ? color : "none"}>
    <rect x="3" y="6" width="26" height="16" rx="4" stroke={color} strokeWidth="2" fill={filled ? color : "none"}/>
    <path d="M8 25L8 22C8 20.8954 8.89543 20 10 20H22C23.1046 20 24 20.8954 24 22V25" stroke={color} strokeWidth="2"/>
  </svg>
);
const SendIcon = ({ color = "#FFC32B", size = 34, filled }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <polygon points="4,28 28,16 4,4 8,16" fill={filled ? color : "none"} stroke={color} strokeWidth="2"/>
  </svg>
);
const GroupsIcon = ({ color = "#FFC32B", size = 34, filled }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <circle cx="10" cy="14" r="4" stroke={color} strokeWidth="2" fill={filled ? color : "none"}/>
    <circle cx="22" cy="14" r="4" stroke={color} strokeWidth="2" fill={filled ? color : "none"}/>
    <ellipse cx="16" cy="22" rx="8" ry="4" stroke={color} strokeWidth="2" fill={filled ? color : "none"}/>
  </svg>
);
const SettingsIcon = ({ color = "#FFC32B", size = 34, filled }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <circle cx="16" cy="16" r="5" stroke={color} strokeWidth="2" fill={filled ? color : "none"}/>
    <path d="M16 3V7M16 25V29M29 16H25M7 16H3M24.07 24.07L21.54 21.54M10.46 10.46L7.93 7.93M24.07 7.93L21.54 10.46M10.46 21.54L7.93 24.07" stroke={color} strokeWidth="2"/>
  </svg>
);

// DATA: example chats
const chats = [
  {
    name: "Bob",
    message: "New unread message!",
    time: "13:10",
    unread: true,
    active: true,
    id: 1,
    conversation: [
      // Empty for Bob as requested
    ],
  },
  {
    name: "Charlie",
    message: "Thanks a lot!",
    time: "15:10",
    unread: false,
    active: false,
    id: 2,
    conversation: [
      { from: "Charlie", text: "Hey! Have you read much about DAOs lately?" },
      { from: "Me", text: "Yes, I've been following some projects. DAOs really change how communities make decisions." },
      { from: "Charlie", text: "Absolutely! I'm working on a proposal for a digital art DAO." },
      { from: "Me", text: "That sounds awesome. Do you already have a governance model in mind?" },
      { from: "Charlie", text: "I'm thinking token-based voting at first, but open to suggestions." },
      { from: "Me", text: "Let me know if you want feedback or help writing the proposal!" },
      { from: "Charlie", text: "I'll send you a draft later today. Thanks a lot!" },
    ],
  },
  {
    name: "Alice",
    message: "Did you get the contract?",
    time: "22:34",
    unread: false,
    active: false,
    id: 3,
    conversation: [
      { from: "Me", text: "Hi Alice! I sent over the documents this morning." },
      { from: "Alice", text: "Great, I'll check my email." },
      { from: "Me", text: "Let me know if you have any questions." },
      { from: "Alice", text: "Will do. Did you get the contract?" },
    ],
  },
];

// Conversation (chat view)
function ChatDetail({ chat, onBack }) {
  return (
    <div style={{
      minHeight: "100vh",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      padding: "28px 0 0 0",
      boxSizing: "border-box",
      position: "relative",
      paddingBottom: 110,
    }}>
      <button onClick={onBack} style={{
        alignSelf: "flex-start",
        marginLeft: 14,
        background: "none",
        border: "none",
        color: "#FFC32B",
        fontSize: 22,
        cursor: "pointer",
        marginBottom: 8,
      }}>‚Üê Back</button>
      <div style={{
        fontFamily: "'Orbitron', sans-serif",
        fontWeight: 700,
        fontSize: 36,
        letterSpacing: "0.04em",
        color: "#FFC32B",
        marginBottom: 18,
        textShadow: "0 0 2px #FFD700, 0 1px 0 #FFD700", // much lighter shadow
      }}>
        {chat.name}
      </div>
      <div style={{
        width: "92%",
        margin: "0 auto",
        flex: 1,
      }}>
        {chat.conversation.length === 0 ? (
          <div style={{
            color: "#666",
            textAlign: "center",
            marginTop: 80,
            fontSize: 22,
          }}>
            No messages yet.
          </div>
        ) : (
          chat.conversation.map((msg, idx) => (
            <div
              key={idx}
              style={{
                textAlign: msg.from === "Me" ? "right" : "left",
                marginBottom: 14,
              }}
            >
              <span
                style={{
                  display: "inline-block",
                  background: msg.from === "Me" ? "#FFC32B" : "#232323",
                  color: msg.from === "Me" ? "#181511" : "#FFC32B",
                  borderRadius: 18,
                  padding: "10px 20px",
                  fontSize: 18,
                  maxWidth: "70%",
                  fontFamily: "'Piedra', cursive",
                  boxShadow: "0 1px 4px #00000033",
                }}
              >
                {msg.text}
              </span>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

// Dummy screens for other menus
function SendScreen() {
  return (
    <div style={{
      minHeight: "calc(100vh - 110px)",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "28px 0 0 0",
      boxSizing: "border-box"
    }}>
      <div style={{
        fontFamily: "'Orbitron', sans-serif",
        fontWeight: 700,
        fontSize: 36,
        letterSpacing: "0.04em",
        color: "#FFC32B",
        marginBottom: 22,
        textShadow: "0 0 2px #FFD700, 0 1px 0 #FFD700",
      }}>
        R&#x039E;AXTIV
      </div>
      <div style={{ fontSize: 32, marginBottom: 14 }}>Send</div>
      <div style={{ fontSize: 18, color: "#fff", textAlign: "center" }}>
        Here you can send messages or tokens to your contacts.<br />
        (Sample screen. Add your own inputs and send logic here.)
      </div>
    </div>
  );
}
function GroupsScreen() {
  return (
    <div style={{
      minHeight: "calc(100vh - 110px)",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "28px 0 0 0",
      boxSizing: "border-box"
    }}>
      <div style={{
        fontFamily: "'Orbitron', sans-serif",
        fontWeight: 700,
        fontSize: 36,
        letterSpacing: "0.04em",
        color: "#FFC32B",
        marginBottom: 22,
        textShadow: "0 0 2px #FFD700, 0 1px 0 #FFD700",
      }}>
        R&#x039E;AXTIV
      </div>
      <div style={{ fontSize: 32, marginBottom: 14 }}>Groups</div>
      <div style={{ fontSize: 18, color: "#fff", textAlign: "center" }}>
        Here you will see your groups and DAOs.<br />
        (Sample screen. Add your group list and features here.)
      </div>
    </div>
  );
}
function SettingsScreen({ onDisconnect }) {
  return (
    <div style={{
      minHeight: "calc(100vh - 110px)",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "28px 0 0 0",
      boxSizing: "border-box"
    }}>
      <div style={{
        fontFamily: "'Orbitron', sans-serif",
        fontWeight: 700,
        fontSize: 36,
        letterSpacing: "0.04em",
        color: "#FFC32B",
        marginBottom: 22,
        textShadow: "0 0 2px #FFD700, 0 1px 0 #FFD700",
      }}>
        R&#x039E;AXTIV
      </div>
      <div style={{ fontSize: 32, marginBottom: 14 }}>Settings</div>
      <div style={{ fontSize: 18, color: "#fff", textAlign: "center" }}>
        Here you can adjust your preferences.<br />
        (Sample screen. Add your user options here.)
      </div>
      <button
        onClick={onDisconnect}
        style={{
          marginTop: 32,
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 18,
          border: "none",
          borderRadius: 24,
          padding: "12px 32px",
          fontFamily: "'Piedra', cursive",
          cursor: "pointer",
          transition: "background 0.2s"
        }}
      >
        Disconnect
      </button>
    </div>
  );
}

export default function ChatsScreen({ onDisconnect }) {
  const [search, setSearch] = useState('');
  const [openedChat, setOpenedChat] = useState(null);
  const [activeMenu, setActiveMenu] = useState('chats');

  // Main panel logic
  let mainPanel;
  if (activeMenu === 'chats') {
    if (openedChat) {
      mainPanel = <ChatDetail chat={openedChat} onBack={() => setOpenedChat(null)} />;
    } else {
      // Chats list
      const filtered = chats.filter(
        c =>
          c.name.toLowerCase().includes(search.toLowerCase()) ||
          c.message.toLowerCase().includes(search.toLowerCase())
      );
      mainPanel = (
        <div style={{ width: "100%", paddingBottom: 110 }}>
          {/* Brand */}
          <div
            style={{
              fontFamily: "'Orbitron', sans-serif",
              fontWeight: 700,
              fontSize: 36,
              letterSpacing: "0.04em",
              color: "#FFC32B",
              marginBottom: 12,
              textShadow: "0 0 2px #FFD700, 0 1px 0 #FFD700",
              textAlign: "center"
            }}
          >
            R&#x039E;AXTIV
          </div>
          {/* Chats title */}
          <div
            style={{
              fontFamily: "'Piedra', cursive",
              fontSize: 36,
              color: "#FFC32B",
              marginBottom: 12,
              marginRight: "auto",
              marginLeft: 36,
            }}
          >
            Chats
          </div>
          {/* Search */}
          <input
            type="text"
            value={search}
            onChange={e => setSearch(e.target.value)}
            placeholder="Search chat, ENS or address"
            style={{
              width: "84%",
              padding: "16px 22px",
              margin: "0 0 18px 0",
              border: "none",
              borderRadius: 28,
              background: "#232323",
              color: "#fff",
              fontSize: 18,
              fontFamily: "'Piedra', cursive",
              outline: "none",
            }}
          />
          {/* Chats list */}
          <div style={{ width: "92%", flex: 1 }}>
            {filtered.map((chat, idx) => (
              <div
                key={chat.id}
                onClick={() => setOpenedChat(chat)}
                style={{
                  display: "flex",
                  alignItems: "center",
                  background: "#232323",
                  borderRadius: 28,
                  padding: "14px 16px",
                  marginBottom: 16,
                  position: "relative",
                  boxShadow: chat.unread
                    ? "0 0 12px #FFC32B33"
                    : "0 1px 2px #00000044",
                  cursor: "pointer",
                  opacity: 1,
                  transition: "background 0.18s, box-shadow 0.18s",
                }}
              >
                {/* Avatar */}
                <div
                  style={{
                    width: 44,
                    height: 44,
                    background: "#FFC32B",
                    borderRadius: "50%",
                    marginRight: 16,
                    flexShrink: 0,
                  }}
                ></div>
                {/* Info */}
                <div style={{ flex: 1 }}>
                  <div
                    style={{
                      fontWeight: 700,
                      fontSize: 20,
                      color: "#FFC32B",
                      fontFamily: "'Piedra', cursive",
                    }}
                  >
                    {chat.name}
                  </div>
                  <div
                    style={{
                      color: chat.unread ? "#FFC32B" : "#fff",
                      fontSize: 17,
                      fontWeight: chat.unread ? 700 : 400,
                      marginTop: 2,
                      fontFamily: "'Piedra', cursive",
                    }}
                  >
                    {chat.message}
                  </div>
                </div>
                {/* Time and status */}
                <div style={{ textAlign: "right", marginLeft: 12 }}>
                  <div
                    style={{
                      color: "#FFC32B",
                      fontSize: 15,
                      fontFamily: "'Piedra', cursive",
                      marginBottom: 2,
                    }}
                  >
                    {chat.time}
                  </div>
                  {/* Online dot */}
                  {chat.active && (
                    <span
                      style={{
                        display: "inline-block",
                        width: 12,
                        height: 12,
                        background: "#23F600",
                        borderRadius: "50%",
                      }}
                    />
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      );
    }
  } else if (activeMenu === 'send') {
    mainPanel = <SendScreen />;
  } else if (activeMenu === 'groups') {
    mainPanel = <GroupsScreen />;
  } else if (activeMenu === 'settings') {
    mainPanel = <SettingsScreen onDisconnect={onDisconnect} />;
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#100E0A",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        boxSizing: "border-box",
        position: "relative"
      }}
    >
      <div style={{ width: "100%", flex: 1 }}>{mainPanel}</div>
      {/* Bottom nav */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          width: "100%",
          position: "fixed",
          bottom: 0,
          left: 0,
          background: "#100E0A",
          padding: "18px 0 9px 0",
          borderTop: "1.5px solid #232323",
          zIndex: 40,
        }}
      >
        <div style={{ textAlign: "center", cursor: "pointer" }} onClick={() => { setActiveMenu('chats'); setOpenedChat(null); }}>
          <ChatIcon filled={activeMenu === 'chats'} color={activeMenu === 'chats' ? "#FFD700" : "#FFC32B"} />
          <div style={{
            color: activeMenu === 'chats' ? "#FFD700" : "#FFC32B",
            fontFamily: "'Piedra', cursive",
            fontSize: 22,
            fontWeight: activeMenu === 'chats' ? 700 : 400
          }}>
            Chats
          </div>
        </div>
        <div style={{ textAlign: "center", cursor: "pointer" }} onClick={() => setActiveMenu('send')}>
          <SendIcon filled={activeMenu === 'send'} color={activeMenu === 'send' ? "#FFD700" : "#FFC32B"} />
          <div style={{
            color: activeMenu === 'send' ? "#FFD700" : "#FFC32B",
            fontFamily: "'Piedra', cursive",
            fontSize: 22,
            fontWeight: activeMenu === 'send' ? 700 : 400
          }}>
            Send
          </div>
        </div>
        <div style={{ textAlign: "center", cursor: "pointer" }} onClick={() => setActiveMenu('groups')}>
          <GroupsIcon filled={activeMenu === 'groups'} color={activeMenu === 'groups' ? "#FFD700" : "#FFC32B"} />
          <div style={{
            color: activeMenu === 'groups' ? "#FFD700" : "#FFC32B",
            fontFamily: "'Piedra', cursive",
            fontSize: 22,
            fontWeight: activeMenu === 'groups' ? 700 : 400
          }}>
            Groups
          </div>
        </div>
        <div style={{ textAlign: "center", cursor: "pointer" }} onClick={() => setActiveMenu('settings')}>
          <SettingsIcon filled={activeMenu === 'settings'} color={activeMenu === 'settings' ? "#FFD700" : "#FFC32B"} />
          <div style={{
            color: activeMenu === 'settings' ? "#FFD700" : "#FFC32B",
            fontFamily: "'Piedra', cursive",
            fontSize: 22,
            fontWeight: activeMenu === 'settings' ? 700 : 400
          }}>
            Settings
          </div>
        </div>
      </div>
    </div>
  );
}
