import React, { useState } from "react";
import WelcomeScreen from "./components/WelcomeScreen";
import { ethers } from "ethers";

function App() {
  const [wallet, setWallet] = useState(null);
  const [provider, setProvider] = useState(null);
  const [error, setError] = useState(null);

  const handleConnect = async () => {
    if (window.ethereum) {
      try {
        const provider = new ethers.BrowserProvider(window.ethereum);
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        const account = accounts[0];
        // Solicita firma para confirmar la identidad del usuario
        const signer = await provider.getSigner();
        const message = "Sign to login to RΞAXTIV";
        await signer.signMessage(message);
        setWallet(account);
        setProvider(provider);
        setError(null);
      } catch (err) {
        setError("Connection rejected or error connecting.");
      }
    } else {
      setError("MetaMask is not installed.");
    }
  };

  if (!wallet) {
    return (
      <div>
        <WelcomeScreen onConnect={handleConnect} />
        {error && (
          <div style={{
            color: "#ff6b6b",
            textAlign: "center",
            marginTop: 16,
            fontFamily: "'Piedra', cursive",
            fontSize: 18
          }}>{error}</div>
        )}
      </div>
    );
  }

  // Aquí va el resto de tu app cuando la wallet está conectada y firmada
  return (
    <div style={{
      minHeight: "100vh",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center"
    }}>
      <h2>Wallet connected and signed!</h2>
      <div style={{ fontFamily: "monospace", fontSize: 22 }}>{wallet}</div>
    </div>
  );
}

export default App;
