import React, { useState } from "react";
import WalletConnect from "./components/WalletConnect";
// Import your other components:
import Settings from "./components/Settings";
// import Chats from "./components/Chats";
// import Groups from "./components/Groups";
// import Send from "./components/Send";

const NAV_SECTIONS = [
  { key: "home", label: "Home" },
  { key: "chats", label: "Chats" },
  { key: "groups", label: "Groups" },
  { key: "send", label: "Send" },
  { key: "settings", label: "Settings" },
];

function App() {
  const [wallet, setWallet] = useState(null);
  const [provider, setProvider] = useState(null);
  const [section, setSection] = useState("home");

  if (!wallet) {
    return (
      <div className="screen-content">
        <h2 className="section-title">Welcome to Reaxtiv</h2>
        <WalletConnect onConnect={(account, prov) => { setWallet(account); setProvider(prov); }} />
      </div>
    );
  }

  // Navigation bar
  const nav = (
    <nav style={{ marginBottom: 16 }}>
      {NAV_SECTIONS.map((s) => (
        <button
          key={s.key}
          onClick={() => setSection(s.key)}
          style={{
            fontWeight: section === s.key ? "bold" : "normal",
            marginRight: 8,
            padding: "6px 16px",
            borderRadius: 4,
            border: "1px solid #ccc",
            background: section === s.key ? "#eee" : "#fff",
            cursor: "pointer"
          }}
        >
          {s.label}
        </button>
      ))}
      <span style={{ float: "right" }}>
        <span style={{ fontFamily: "monospace" }}>{wallet}</span>
        <button style={{ marginLeft: 12 }} onClick={() => { setWallet(null); setProvider(null); setSection("home"); }}>
          Disconnect
        </button>
      </span>
    </nav>
  );

  let content;
  switch (section) {
    case "home":
      content = <div>Welcome to Reaxtiv! Select a section.</div>;
      break;
    case "settings":
      content = <Settings wallet={wallet} provider={provider} onLogout={() => { setWallet(null); setProvider(null); setSection("home"); }} />;
      break;
    // case "chats":
    //   content = <Chats wallet={wallet} provider={provider} />;
    //   break;
    // case "groups":
    //   content = <Groups wallet={wallet} provider={provider} />;
    //   break;
    // case "send":
    //   content = <Send wallet={wallet} provider={provider} />;
    //   break;
    default:
      content = <div>Coming soon...</div>;
  }

  return (
    <div className="screen-content">
      {nav}
      {content}
    </div>
  );
}

export default App;
