import React, { useState } from "react";
import WalletConnect from "./components/WalletConnect";
import Settings from "./components/Settings";
// Importa otros componentes si los tienes, por ejemplo:
// import Chats from "./components/Chats";
// import Groups from "./components/Groups";
// import Send from "./components/Send";

function App() {
  const [wallet, setWallet] = useState(null);
  const [provider, setProvider] = useState(null);

  // Si no hay wallet conectada, muestra el botón de conectar
  if (!wallet) {
    return (
      <div className="screen-content">
        <h2 className="section-title">Bienvenido a Reaxtiv</h2>
        <WalletConnect onConnect={(account, prov) => { setWallet(account); setProvider(prov); }} />
      </div>
    );
  }

  // Aquí puedes mostrar el resto de tu app después de conectar MetaMask
  return (
    <div className="screen-content">
      <h2 className="section-title">¡Wallet conectada!</h2>
      <div style={{ margin: "18px 0" }}>
        <strong>Dirección:</strong> <span style={{ fontFamily: "monospace" }}>{wallet}</span>
      </div>
      {/* Aquí puedes renderizar tus otros componentes y pasarles wallet y provider si lo necesitan */}
      {/* <Chats wallet={wallet} provider={provider} /> */}
      {/* <Groups wallet={wallet} provider={provider} /> */}
      {/* <Send wallet={wallet} provider={provider} /> */}
      <Settings wallet={wallet} onLogout={() => { setWallet(null); setProvider(null); }} />
    </div>
  );
}

export default App;
