import React, { useState } from "react";
import WalletSigned from "./components/WalletSigned";
import ChatsScreen from "./components/ChatsScreen";

export default function App() {
  const [wallet, setWallet] = useState(null);
  const [signed, setSigned] = useState(false);

  // Logo local desde /public/logo.png (sin recuadro, sombra solo en imagen)
  const Logo = () => (
    <img
      src="/logo.png"
      alt="logo"
      style={{
        width: 220,
        marginBottom: 36,
        filter: "drop-shadow(0 0 32px #FFD700)"
      }}
    />
  );

  // Conexión y firma en Metamask
  const connectWallet = async () => {
    if (window.ethereum && window.ethereum.isMetaMask) {
      try {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        if (accounts && accounts[0]) {
          // Solicita firma para identificar (opcional pero recomendado en Web3 UX)
          try {
            const message = "Sign to enter RΞAXTIV. This proves wallet ownership.";
            await window.ethereum.request({
              method: "personal_sign",
              params: [message, accounts[0]],
            });
            setWallet(accounts[0]);
          } catch (signError) {
            alert("You must sign the message in MetaMask to continue.");
          }
        }
      } catch (err) {
        alert("Connection rejected.");
      }
    } else {
      alert("Please install MetaMask extension in your browser.");
    }
  };

  const disconnect = () => {
    setWallet(null);
    setSigned(false);
  };

  // Pantalla de bienvenida
  if (!wallet) {
    return (
      <div
        style={{
          minHeight: "100vh",
          background: "#000000",
          color: "#FFC32B",
          fontFamily: "'Piedra', cursive",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Logo />
        <h1
          style={{
            fontFamily: "'Orbitron', sans-serif",
            fontWeight: 700,
            fontSize: 54,
            letterSpacing: "0.04em",
            color: "#FFC32B",
            marginBottom: 10,
            textShadow: "0 2px 10px #FFD700",
          }}
        >
          RΞAXTIV
        </h1>
        <div
          style={{
            fontFamily: "'Piedra', cursive",
            fontSize: 34,
            marginBottom: 44,
            color: "#FFC32B",
            textAlign: "center",
            maxWidth: 420,
            lineHeight: 1.18
          }}
        >
          Code your voice
        </div>
        <button
          onClick={connectWallet}
          style={{
            background: "#FFC32B",
            color: "#181511",
            fontWeight: 700,
            fontSize: 28,
            border: "none",
            borderRadius: 40,
            padding: "20px 60px",
            fontFamily: "'Piedra', cursive",
            cursor: "pointer",
            marginTop: 12,
          }}
        >
          Connect Wallet
        </button>
        <div
          style={{
            color: "#fff",
            fontWeight: 400,
            fontSize: 22,
            marginTop: 38,
            textAlign: "center",
            fontFamily: "'Piedra', cursive",
            maxWidth: 400,
            lineHeight: "1.25",
            opacity: 0.7
          }}
        >
          Your privacy, your control. No email or phone required.
        </div>
      </div>
    );
  }

  // Pantalla de wallet firmada
  if (wallet && !signed) {
    return (
      <WalletSigned
        wallet={wallet}
        onOk={() => setSigned(true)}
        onDisconnect={disconnect}
      />
    );
  }

  // Pantalla principal de chats
  return <ChatsScreen onDisconnect={disconnect} />;
}
