import React, { useState } from "react";

// SVG ICONS
const ChatIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <rect x="3" y="6" width="26" height="16" rx="4" stroke={color} strokeWidth="2"/>
    <path d="M8 25L8 22C8 20.8954 8.89543 20 10 20H22C23.1046 20 24 20.8954 24 22V25" stroke={color} strokeWidth="2"/>
  </svg>
);
const SendIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <polygon points="4,28 28,16 4,4 8,16" fill="none" stroke={color} strokeWidth="2"/>
  </svg>
);
const GroupsIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <circle cx="10" cy="14" r="4" stroke={color} strokeWidth="2"/>
    <circle cx="22" cy="14" r="4" stroke={color} strokeWidth="2"/>
    <ellipse cx="16" cy="22" rx="8" ry="4" stroke={color} strokeWidth="2"/>
  </svg>
);
const SettingsIcon = ({ color = "#FFC32B", size = 34 }) => (
  <svg width={size} height={size} viewBox="0 0 32 32" fill="none">
    <circle cx="16" cy="16" r="5" stroke={color} strokeWidth="2"/>
    <path d="M16 3V7M16 25V29M29 16H25M7 16H3M24.07 24.07L21.54 21.54M10.46 10.46L7.93 7.93M24.07 7.93L21.54 10.46M10.46 21.54L7.93 24.07" stroke={color} strokeWidth="2"/>
  </svg>
);

// DATA: example chats
const chats = [
  {
    name: "Bob",
    message: "New unread message!",
    time: "13:10",
    unread: true,
    active: true,
    id: 1,
  },
  {
    name: "Charlie",
    message: "Thanks a lot!",
    time: "15:10",
    unread: false,
    active: false,
    id: 2,
  },
  {
    name: "Alice",
    message: "Did you get the contract?",
    time: "22:34",
    unread: false,
    active: false,
    id: 3,
  },
];

// Componente para la pantalla de conversación (simple demo)
function ChatDetail({ chat, onBack }) {
  return (
    <div style={{
      minHeight: "100vh",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      padding: "28px 0 0 0",
      boxSizing: "border-box",
    }}>
      <button onClick={onBack} style={{
        alignSelf: "flex-start",
        marginLeft: 14,
        background: "none",
        border: "none",
        color: "#FFC32B",
        fontSize: 22,
        cursor: "pointer"
      }}>← Back</button>
      <div style={{
        fontFamily: "'Orbitron', sans-serif",
        fontWeight: 700,
        fontSize: 36,
        letterSpacing: "0.04em",
        color: "#FFC32B",
        marginBottom: 12,
        textShadow: "0 0 12px #FFC32B99, 1px 1px 0px #FFD700",
      }}>
        {chat.name}
      </div>
      <div style={{
        background: "#232323",
        borderRadius: 28,
        padding: "18px 22px",
        margin: "20px 0",
        fontSize: 22,
        minWidth: 250,
        color: "#fff"
      }}>
        {chat.message}
      </div>
      <div style={{ color: "#FFC32B", fontSize: 16 }}>{chat.time}</div>
    </div>
  );
}

export default function ChatsScreen({ onDisconnect }) {
  const [search, setSearch] = useState("");
  const [openedChat, setOpenedChat] = useState(null);

  if (openedChat) {
    return <ChatDetail chat={openedChat} onBack={() => setOpenedChat(null)} />;
  }

  // Filtra chats por búsqueda
  const filtered = chats.filter(
    c =>
      c.name.toLowerCase().includes(search.toLowerCase()) ||
      c.message.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#100E0A",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "28px 0 0 0",
        boxSizing: "border-box",
        position: "relative",
        paddingBottom: 110, // espacio para nav
      }}
    >
      {/* Brand */}
      <div
        style={{
          fontFamily: "'Orbitron', sans-serif",
          fontWeight: 700,
          fontSize: 36,
          letterSpacing: "0.04em",
          color: "#FFC32B",
          marginBottom: 12,
          textShadow: "0 0 12px #FFC32B99, 1px 1px 0px #FFD700",
        }}
      >
        R&#x039E;AXTIV
      </div>
      {/* Chats title */}
      <div
        style={{
          fontFamily: "'Piedra', cursive",
          fontSize: 36,
          color: "#FFC32B",
          textShadow: "0 1px 8px #FFD700, 0 0px 8px #FFC32B44",
          marginBottom: 12,
          marginRight: "auto",
          marginLeft: 36,
        }}
      >
        Chats
      </div>
      {/* Search */}
      <input
        type="text"
        value={search}
        onChange={e => setSearch(e.target.value)}
        placeholder="Search chat, ENS or address"
        style={{
          width: "84%",
          padding: "16px 22px",
          margin: "0 0 18px 0",
          border: "none",
          borderRadius: 28,
          background: "#232323",
          color: "#fff",
          fontSize: 18,
          fontFamily: "'Piedra', cursive",
          outline: "none",
        }}
      />
      {/* Chats list */}
      <div style={{ width: "92%", flex: 1 }}>
        {filtered.map((chat, idx) => (
          <div
            key={chat.id}
            onClick={() => setOpenedChat(chat)}
            style={{
              display: "flex",
              alignItems: "center",
              background: "#232323",
              borderRadius: 28,
              padding: "14px 16px",
              marginBottom: 16,
              position: "relative",
              boxShadow: chat.unread
                ? "0 0 12px #FFC32B33"
                : "0 1px 2px #00000044",
              cursor: "pointer",
              opacity: 1,
              transition: "background 0.18s, box-shadow 0.18s",
            }}
          >
            {/* Avatar */}
            <div
              style={{
                width: 44,
                height: 44,
                background: "#FFC32B",
                borderRadius: "50%",
                marginRight: 16,
                flexShrink: 0,
              }}
            ></div>
            {/* Info */}
            <div style={{ flex: 1 }}>
              <div
                style={{
                  fontWeight: 700,
                  fontSize: 20,
                  color: "#FFC32B",
                  fontFamily: "'Piedra', cursive",
                }}
              >
                {chat.name}
              </div>
              <div
                style={{
                  color: chat.unread ? "#FFC32B" : "#fff",
                  fontSize: 17,
                  fontWeight: chat.unread ? 700 : 400,
                  marginTop: 2,
                  fontFamily: "'Piedra', cursive",
                }}
              >
                {chat.message}
              </div>
            </div>
            {/* Time and status */}
            <div style={{ textAlign: "right", marginLeft: 12 }}>
              <div
                style={{
                  color: "#FFC32B",
                  fontSize: 15,
                  fontFamily: "'Piedra', cursive",
                  marginBottom: 2,
                }}
              >
                {chat.time}
              </div>
              {/* Online dot */}
              {chat.active && (
                <span
                  style={{
                    display: "inline-block",
                    width: 12,
                    height: 12,
                    background: "#23F600",
                    borderRadius: "50%",
                  }}
                />
              )}
            </div>
          </div>
        ))}
      </div>
      {/* Bottom nav */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          width: "100%",
          position: "fixed",
          bottom: 0,
          left: 0,
          background: "#100E0A",
          padding: "18px 0 9px 0",
          borderTop: "1.5px solid #232323",
          zIndex: 40,
        }}
      >
        <div style={{ textAlign: "center" }}>
          <ChatIcon />
          <div style={{ color: "#FFC32B", fontFamily: "'Piedra', cursive", fontSize: 22 }}>
            Chats
          </div>
        </div>
        <div style={{ textAlign: "center" }}>
          <SendIcon />
          <div style={{ color: "#FFC32B", fontFamily: "'Piedra', cursive", fontSize: 22 }}>
            Send
          </div>
        </div>
        <div style={{ textAlign: "center" }}>
          <GroupsIcon />
          <div style={{ color: "#FFC32B", fontFamily: "'Piedra', cursive", fontSize: 22 }}>
            Groups
          </div>
        </div>
        <div style={{ textAlign: "center" }}>
          <SettingsIcon />
          <div style={{ color: "#FFC32B", fontFamily: "'Piedra', cursive", fontSize: 22 }}>
            Settings
          </div>
        </div>
      </div>
      {/* Botón desconectar (opcional, fuera del nav) */}
      <button
        onClick={onDisconnect}
        style={{
          position: "fixed",
          top: 18,
          right: 18,
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 16,
          border: "none",
          borderRadius: 24,
          padding: "8px 22px",
          fontFamily: "'Piedra', cursive",
          cursor: "pointer",
          transition: "background 0.2s",
          zIndex: 50
        }}
      >
        Disconnect
      </button>
    </div>
  );
}
