import React from "react";
import "../App.css";

function shortWallet(address) {
  if (!address) return "";
  return address.slice(0, 6) + "..." + address.slice(-4);
}

export default function WalletSigned({ wallet, onOk, onDisconnect }) {
  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#181511",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        fontSize: 70,
        padding: 0,
      }}
    >
      <h2 style={{ marginBottom: 62, fontSize: 82, fontWeight: 700 }}>
        Wallet connected and signed!
      </h2>
      <div
        style={{
          fontFamily: "monospace",
          fontSize: 70,
          marginBottom: 62,
          background: "#181511",
          borderRadius: 26,
          padding: "28px 78px",
          color: "#FFC32B",
          letterSpacing: "1px",
        }}
      >
        {shortWallet(wallet)}
      </div>
      <button
        className="big-button"
        onClick={onOk}
      >
        OK
      </button>
      <button
        className="big-button"
        onClick={onDisconnect}
      >
        Disconnect
      </button>
      <div
        style={{
          color: "#fff",
          fontWeight: 400,
          fontSize: 54,
          marginTop: 62,
          textAlign: "center",
          fontFamily: "'Piedra', cursive",
          maxWidth: 900,
          lineHeight: "1.25",
        }}
      >
        Your privacy, your control. No email or phone required
      </div>
    </div>
  );
}
