import React, { useState } from "react";
import WelcomeScreen from "./components/WelcomeScreen";
import { ethers } from "ethers";

// Función para abreviar la wallet
function shortWallet(address) {
  if (!address) return "";
  return address.slice(0, 5) + "..." + address.slice(-5);
}

function App() {
  const [wallet, setWallet] = useState(null);
  const [provider, setProvider] = useState(null);
  const [error, setError] = useState(null);
  const [showMenu, setShowMenu] = useState(false);

  const handleConnect = async () => {
    if (window.ethereum) {
      try {
        const provider = new ethers.BrowserProvider(window.ethereum);
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        const account = accounts[0];
        const signer = await provider.getSigner();
        const message = "Sign to login to RΞAXTIV";
        await signer.signMessage(message);
        setWallet(account);
        setProvider(provider);
        setError(null);
        setShowMenu(false);
      } catch (err) {
        setError("Connection rejected or error connecting.");
      }
    } else {
      setError("MetaMask is not installed.");
    }
  };

  const handleDisconnect = () => {
    setWallet(null);
    setProvider(null);
    setError(null);
    setShowMenu(false);
  };

  const handleOk = () => {
    setShowMenu(true);
  };

  // Pantalla de bienvenida
  if (!wallet && !showMenu) {
    return (
      <div>
        <WelcomeScreen onConnect={handleConnect} />
        {error && (
          <div style={{
            color: "#ff6b6b",
            textAlign: "center",
            marginTop: 16,
            fontFamily: "'Piedra', cursive",
            fontSize: 18
          }}>{error}</div>
        )}
      </div>
    );
  }

  // Menú principal tras pulsar OK
  if (showMenu) {
    return (
      <div style={{
        minHeight: "100vh",
        background: "#100E0A",
        color: "#FFC32B",
        fontFamily: "'Piedra', cursive",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center"
      }}>
        <h2>¡Bienvenido al menú principal!</h2>
        <p>Aquí puedes implementar la navegación a tus distintos menús.</p>
        <button
          onClick={handleDisconnect}
          style={{
            background: "#FFC32B",
            color: "#181511",
            fontWeight: 700,
            fontSize: 18,
            border: "none",
            borderRadius: 30,
            padding: "12px 32px",
            marginTop: 16,
            fontFamily: "'Piedra', cursive",
            cursor: "pointer",
            transition: "background 0.2s"
          }}
        >
          Disconnect Wallet
        </button>
      </div>
    );
  }

  // Pantalla de wallet conectada y firmada, con botón OK
  return (
    <div style={{
      minHeight: "100vh",
      background: "#100E0A",
      color: "#FFC32B",
      fontFamily: "'Piedra', cursive",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center"
    }}>
      <h2 style={{ marginBottom: 16 }}>Wallet connected and signed!</h2>
      <div style={{
        fontFamily: "monospace",
        fontSize: 22,
        marginBottom: 18,
        background: "#181511",
        borderRadius: 12,
        padding: "8px 24px",
        color: "#FFC32B",
        letterSpacing: "1px"
      }}>
        {shortWallet(wallet)}
      </div>
      <button
        onClick={handleOk}
        style={{
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 18,
          border: "none",
          borderRadius: 30,
          padding: "12px 32px",
          marginTop: 8,
          fontFamily: "'Piedra', cursive",
          cursor: "pointer",
          transition: "background 0.2s"
        }}
      >
        OK
      </button>
      <button
        onClick={handleDisconnect}
        style={{
          background: "#FFC32B",
          color: "#181511",
          fontWeight: 700,
          fontSize: 18,
          border: "none",
          borderRadius: 30,
          padding: "12px 32px",
          marginTop: 8,
          fontFamily: "'Piedra', cursive",
          cursor: "pointer",
          transition: "background 0.2s"
        }}
      >
        Disconnect
      </button>
      <div style={{
        color: "#fff",
        fontWeight: 400,
        fontSize: 16,
        marginTop: 18,
        textAlign: "center",
        textShadow: "0 1px 2px #000",
        fontFamily: "'Piedra', cursive",
        maxWidth: 340,
        lineHeight: "1.25"
      }}>
        Your privacy, your control. No email or phone required
      </div>
    </div>
  );
}

export default App;
